package Booking;

public class Lr {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l=2,r=5;
		int i=1;
	
		while(true){
			i++;
			if(i%l==0 && i%r==0){
				
				System.out.print(i);
				
				break;
				
			}
			
			
			
		}
		

		
		
		
	}

}
